public class subsequence {
    public static void main(String[] args) {
        String A = "";

        int mod = 1000000007;
        int countG = 0;
        int ans = 0;
        for (int i = A.length() - 1; i >= 0 ; i--){
            if(A.charAt(i) == 'G'){
                countG++;
            }else if(A.charAt(i) == 'A'){
                ans = (ans + countG) % mod;
            }

        }
        System.out.println(ans);
    }
}
